jQuery(document).ready(function() {
    var adjustHeroSection = function() {
        var navHeight = jQuery('nav').outerHeight();
        var toggleTarget = jQuery('[data-toggle-target]');
        var heroSection = jQuery('.hero');

        if (toggleTarget.hasClass('toggle-target-expanded')) {
            heroSection.css('margin-top','0px');
        } else {
          
            heroSection.css('margin-top', navHeight - toggleTarget.outerHeight() + 'px');
        }
    };

    var toggleFunction = function(element) {
        var text = jQuery(element).text();
        var target = jQuery(element).attr("data-toggle-target");

        jQuery(element).addClass("toggle-btn-visible");
        jQuery(target).addClass("toggle-target-hidden");

        jQuery(element).click(function() {
            if (jQuery(element).text() === jQuery(element).attr("data-toggle-text")) {
                jQuery(element).text(text);
            } else {
                jQuery(element).text(jQuery(element).attr("data-toggle-text"));
            }
            jQuery(target).slideToggle(400, function() {
                jQuery(target).toggleClass("toggle-target-expanded").css("display", "");
                adjustHeroSection(); // Call the function to adjust the hero section
            });
        });
    };

    if (jQuery("[data-toggle-target]").length) {
        jQuery("[data-toggle-target]").each(function() {
            toggleFunction(this);
        });
    }
});
